// console.log(doubleSequence(5, 4));  // [5, 10, 20, 40]
// console.log(doubleSequence(5, 0));  // [ ]

/******************** DO NOT MODIFY ANY CODE BELOW THIS LINE *****************/
module.exports = doubleSequence;